function Ball(a,b){this.xPos=a;this.yPos=b;this.xVel=10*Math.random()-5;this.yVel=10*Math.random()-5;this.move=function(a,b){this.xPos+=this.xVel;this.yPos+=this.yVel;this.xPos+50>a?this.xVel=-this.xVel:0>this.xPos&&(this.xVel=-this.xVel);this.yPos+50>b?this.yVel=-this.yVel:0>this.yPos&&(this.yVel=-this.yVel)}};
